import React, { useState } from 'react';

const RelationPage = () => {
  const [person1, setPerson1] = useState('');
  const [person2, setPerson2] = useState('');
  const [relationship, setRelationship] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('/api/relationship', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ person1, person2 }),
    });
    const data = await response.json();
    setRelationship(data.relationship || 'لم يتم إيجاد علاقة.');
  };

  return (
    <div className="relation-page">
      <h2>ماهي العلاقة بينهما؟</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="الإسم الكامل الأول" value={person1} onChange={(e) => setPerson1(e.target.value)} />
        <input type="text" placeholder="الإسم الكامل الثاني" value={person2} onChange={(e) => setPerson2(e.target.value)} />
        <button type="submit">تحقق من العلاقة</button>
      </form>
      {relationship && <p>العلاقة: {relationship}</p>}
    </div>
  );
};

export default RelationPage;
