import React, { useState, useEffect } from 'react';
import neo4j from 'neo4j-driver';
import "./SearchPage.css"
const translations = require('./translation.json');
require('dotenv').config();

const driver = neo4j.driver(
  'neo4j+s://2cd0ce39.databases.neo4j.io',  // URI of the Neo4j server
  neo4j.auth.basic('neo4j', 'nW1azrzTK-lrTOO5G1uOkUVFwelcQlEmKPHggPUB7xQ'));

export const translateName = (fullName, language = true) => {
  const nameParts = fullName.split(' ');

const reverseTranslations = Object.fromEntries(
  Object.entries(translations).map(([key, value]) => [value, key])
);

  const dict = language ? translations : reverseTranslations;

  const translatedParts = nameParts.map(part => dict[part] || part);

  return translatedParts.join(' ');
};

function splitName(fullName) {
  const parts = fullName.replace(/ ben /gi, ' ').split(/\s+/);
  if (parts.length === 2) {
    return {
      personName: parts[0],
      fatherName: "",
      grandfatherName: "",
      familyName: parts[1]
    };
  } else if (parts.length === 3) {
    return {
      personName: parts[0],
      fatherName: parts[1],
      grandfatherName: "",
      familyName: parts[2]
    };
  } else if (parts.length === 4) {
    return {
      personName: parts[0],
      fatherName: parts[1],
      grandfatherName: parts[2],
      familyName: parts[3]
    };
  }
  // Default case if structure doesn't match
  return { personName: parts[0], fatherName: "", grandfatherName: "", familyName: parts[1] };
}


const SearchPage = () => {

  const [searchQuery, setSearchQuery] = useState('');
  const [personDetails, setPersonDetails] = useState(null);
  const [error, setError] = useState('');
  
  const getChildrenOfFather = async (fatherId) => {
    const session = driver.session();
    try {
      const result = await session.run(
        `
        MATCH (father:Person)
        WHERE id(father) = $fatherId
        OPTIONAL MATCH (father)-[:FATHER_OF]->(child:Person)
        RETURN father, collect(child) AS children
        `,
        { fatherId }
      );
  
      if (result.records.length > 0) {
        const record = result.records[0];
        const fatherNode = record.get('father');
        const childrenNodes = record.get('children');
  
        const father = {
          id: fatherNode.identity.toNumber(),
          ...fatherNode.properties,
        };
  
        const children = childrenNodes
          .filter(child => child) // filter out nulls if no children
          .map(child => ({
            id: child.identity.toNumber(),
            ...child.properties,
          }));
  
        return { father, children };
      } else {
        return null;
      }
    } catch (error) {
      console.error('Error fetching father and children:', error);
      return null;
    } finally {
      await session.close();
    }
  };
  
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handlePersonSelect = (selectedPerson) => {
    // When the user selects a match, update the state to show detailed information about the selected person
    setPersonDetails({
      personName: selectedPerson.personName,
      fatherName: selectedPerson.fatherName,
      grandfatherName: selectedPerson.grandfatherName,
      familyName: selectedPerson.familyName,
      gender: selectedPerson.gender,
      age: selectedPerson.age,  // You can calculate the age in the same way as before
    });
  };

  const handleSearchSubmit = async () => {
    if (!searchQuery.trim()) {
      setError('ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ŸÜÿµ ŸÑŸÑÿ®ÿ≠ÿ´.');
      setPersonDetails(null);
      return;
    }
    await searchPerson(searchQuery.trim());
  };

  const searchPerson = async (searchText) => {
    let translatedInputName = translateName(searchText, false);
    console.log(translatedInputName);
    const { personName, fatherName, grandfatherName, familyName } = splitName(translatedInputName);


    let cypherQuery = `
      MATCH (grandfather:Person)-[:FATHER_OF]->(father:Person)-[:FATHER_OF]->(child:Person)
      WHERE 1=1
    `;

    const queryParamsObject = {};

    if (personName) {
      cypherQuery += ` AND child.name = $personName`;
      queryParamsObject.personName = personName;
    }

    if (fatherName) {
      cypherQuery += ` AND father.name = $fatherName`;
      queryParamsObject.fatherName = fatherName;
    }

    if (grandfatherName) {
      cypherQuery += ` AND grandfather.name = $grandfatherName`;
      queryParamsObject.grandfatherName = grandfatherName;
    }

    if (familyName) {
      cypherQuery += ` AND child.lastName = $familyName`;
      queryParamsObject.familyName = familyName;
    }
    
    cypherQuery += `
      OPTIONAL MATCH (motherGrandfather)-[:FATHER_OF]->(motherfather)-[:FATHER_OF]->(mother:Person)-[:MOTHER_OF]->(child:Person)
      RETURN
        id(child) as childID, 
        child.name AS childName, father.name AS fatherName, grandfather.name AS grandfatherName, 
        child.YoB AS childYoB, 
        child.gender AS childGender, 
        child.lastName AS familyName,
        id(mother) AS motherID,
        mother.name as motherName, motherfather.name AS motherFatherName, motherGrandfather.name AS motherGrandfatherName, mother.lastName AS motherFamilyName, child.isAlive AS lifeStatus
    `;

    const session = driver.session();
    try {
      const result = await session.run(cypherQuery, queryParamsObject);
      
      if (result.records.length == 1) {
        const record = result.records[0];
        const age = new Date().getFullYear() - record.get('childYoB');
        const childID = record.get("childID").toNumber();
        const childrenCountRecord = await session.run( `
          MATCH (p:Person)-[:FATHER_OF|MOTHER_OF]->(child:Person)
          WHERE id(p) = $childID
          RETURN count(child) AS childrenCount
        `, {childID});
        const isMarried = await session.run(`
          MATCH (m:Person)-[r:HUSBAND_OF]->(w:Person)
          WHERE id(m) = $childID
          RETURN count(r) > 0 AS isMarried

        `,{ childID });
        console.log(record.get('motherID').toNumber());
        setPersonDetails({
          personID: childID,
          personName: record.get('childName'),
          fatherName: record.get('fatherName'),
          grandfatherName: record.get('grandfatherName'),
          familyName: record.get('familyName'),
          gender: record.get('childGender'),
          age,
          motherName: record.get('motherName') ?? "ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±",
          motherFatherName: record.get('motherFatherName') ?? "ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±",
          motherGrandFatherName: record.get('motherGrandfatherName') ?? "ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±",
          motherFamilyName: record.get('motherFamilyName') ?? "ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±",
          lifeStatus: record.get('lifeStatus'),
          childrenCount : childrenCountRecord.records[0].get('childrenCount').toInt()
        });
        

        setError('');
      }
      else if (result.records.length >= 2) {
        // Prepare a list of matching persons to show
        const multipleMatches = result.records.map(record => ({
          personName: record.get('childName'),
          fatherName: record.get('fatherName'),
          grandfatherName: record.get('grandfatherName'),
          familyName: record.get('familyName'),
        }));
      
        setPersonDetails({ multipleMatches });
        setError('ŸáŸÜÿßŸÉ ÿßŸÑÿπÿØŸäÿØ ŸÖŸÜ ÿßŸÑÿ£ÿ¥ÿÆÿßÿµ Ÿäÿ≠ŸÖŸÑŸàŸÜ ŸÜŸÅÿ≥ ÿßŸÑÿßÿ≥ŸÖ. ÿßŸÑÿ±ÿ¨ÿßÿ° ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿ¥ÿÆÿµ ÿßŸÑÿµÿ≠Ÿäÿ≠.');
      }
      else {
        setPersonDetails(null);
        setError('ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ¥ÿÆÿµ ŸÖÿ∑ÿßÿ®ŸÇ.');
      }
    } catch (err) {
      console.error('Query Error:', err);
      setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ®ÿ≠ÿ´.');
      setPersonDetails(null);
    } finally {
      await session.close();
    }
  };

  return (
    <div className="search-page">
      <header className="search-header">
        <h1>ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ÿ¥ÿÆÿµ ŸÅŸä ÿ¥ÿ¨ÿ±ÿ© ÿßŸÑÿπÿßÿ¶ŸÑÿ©</h1>
        <input
          type="text"
          className="search-bar"
          placeholder="ÿßÿØÿÆŸÑ ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ´ŸÑÿßÿ´Ÿä ÿ£Ÿà ÿßŸÑÿ±ÿ®ÿßÿπŸä..."
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button className="search-button" onClick={handleSearchSubmit}>ÿßÿ®ÿ≠ÿ´</button>
      </header>

      {error && <div className="error-message">{error}</div>}

      {personDetails && personDetails.multipleMatches ? (
  <div className="multiple-matches">
    <h2>ŸÜÿ™ÿßÿ¶ÿ¨ ŸÖÿ™ÿπÿØÿØÿ©:</h2>
    {personDetails.multipleMatches.map((person, index) => (
      <div 
        key={index} 
        className="match-item"
        onClick={() => handlePersonSelect(person)}
        style={{ cursor: 'pointer' }}
      >
        <p><p className='dupPersonName'>{index + 1}- {translateName(person.personName)} ÿ®ŸÜ {translateName(person.fatherName)} ÿ®ŸÜ {translateName(person.grandfatherName)} {translateName(person.familyName)}</p></p>
        <hr />
      </div>
    ))}
  </div>
) : personDetails ? (
  <div className="person-details">
    <h2>ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ¥ÿÆÿµ :</h2>
    <h3>
      {console.log(getChildrenOfFather)}
      {translateName(personDetails.personName)} ÿ®ŸÜ {translateName(personDetails.fatherName)} ÿ®ŸÜ {translateName(personDetails.grandfatherName)} {translateName(personDetails.familyName)}
      </h3>
    <table className="person-details-table">
      <thead>
        <tr>
          <th className='blankHeader'></th>
          <th>ÿßŸÑÿ•ÿ≥ŸÖ</th>
          <th>ÿ•ÿ≥ŸÖ ÿßŸÑÿ£ÿ®</th>
          <th>ÿ•ÿ≥ŸÖ ÿßŸÑÿ¨ŸëÿØ</th>
          <th>ÿ•ÿ≥ŸÖ ÿßŸÑÿπÿßÿ¶ŸÑÿ© / ÿßŸÑŸÑŸÇÿ®</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td><strong>ÿßŸÑÿ¥ÿÆÿµ</strong></td>
          <td><p className='personDetails'>{translateName(personDetails.personName) || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}</p></td>
          <td><p className='personDetails'>{translateName(personDetails.fatherName) || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}</p></td>
          <td><p className='personDetails'>{translateName(personDetails.grandfatherName) || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}</p></td>
          <td><p className='personDetails'>{translateName(personDetails.familyName) || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}</p></td>
        </tr>
        <tr>
          <td><strong>ÿßŸÑÿ£ŸÖ</strong></td>
          <td><p className='personDetails'>{translateName(personDetails.motherName) || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}</p></td>
          <td><p className='personDetails'>{translateName(personDetails.motherFatherName) || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}</p></td>
          <td><p className='personDetails'>{translateName(personDetails.motherGrandFatherName) || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}</p></td>
          <td><p className='personDetails'>{translateName(personDetails.motherFamilyName) || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}</p></td>
        </tr>
      </tbody>
    </table>
    <table className="person-details2-table">
      <thead>
        <tr>
          <th>ÿßŸÑÿ¨ŸÜÿ≥</th>
          <th>ÿßŸÑÿπŸÖÿ±</th>
          <th>ÿßŸÑÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿØŸÜŸäÿ©</th>
          <th>ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≠Ÿäÿßÿ©</th>
          <th>ÿπÿØÿØ ÿßŸÑÿ£ÿ∑ŸÅÿßŸÑ</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>{personDetails.gender === 'Male' ? 'ÿ∞ŸÉÿ± üë®'  : 'ÿ£ŸÜÿ´Ÿâ üë©'}</td>
          <td>
            {personDetails.age === 1
              ? 'ÿ≥ŸÜÿ© Ÿàÿßÿ≠ÿØÿ©'
              : personDetails.age === 2
              ? 'ÿ≥ŸÜÿ™ÿßŸÜ'
              : personDetails.age >= 3 && personDetails.age <= 10
              ? `${personDetails.age} ÿ≥ŸÜŸàÿßÿ™`
              : `${personDetails.age} ÿ≥ŸÜÿ©`}
          </td>
          <td>
            {personDetails.isMarried === 1
              ? (personDetails.gender === 'Male' ? 'ŸÖÿ™ÿ≤Ÿàÿ¨' : 'ŸÖÿ™ÿ≤Ÿàÿ¨ÿ©')
              : (personDetails.gender === 'Male' ? 'ÿ£ÿπÿ≤ÿ®' : 'ÿπÿ≤ÿ®ÿßÿ°')}
          </td>
          <td>
          {personDetails.lifeStatus === true 
            ? (personDetails.gender === 'Male' ? 'ÿ≠Ÿä' : 'ÿ≠Ÿäÿ©') 
            : (personDetails.gender === 'Male' ? 'ŸÖÿ™ŸàŸÅŸâ' : 'ŸÖÿ™ŸàŸÅŸäÿ©')}
        </td>
        <td>
        <button
          onClick={async (e) => {
            e.stopPropagation();
            const children = await getChildrenOfFather(personDetails.childID);
            console.log(children);
          }}
        >
          {personDetails.childrenCount === 0 || personDetails.childrenCount == null
            ? '0'
            : personDetails.childrenCount === 1
            ? 'ÿ∑ŸÅŸÑ Ÿàÿßÿ≠ÿØ'
            : personDetails.childrenCount === 2
            ? 'ÿ∑ŸÅŸÑÿßŸÜ'
            : personDetails.childrenCount >= 3 && personDetails.childrenCount <= 10
            ? `${personDetails.childrenCount} ÿ£ÿ∑ŸÅÿßŸÑ`
            : `${personDetails.childrenCount} ÿ∑ŸÅŸÑÿß`
          }
        </button>
      </td>
        </tr>
      </tbody>
    </table>
    
  </div>
) : (
  <div className="no-results">
    <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÜÿ™ÿßÿ¶ÿ¨ ŸÑŸÑÿ®ÿ≠ÿ´.</p>
  </div>
)}
  <div>
    
  </div>
    </div>
  );
};

export default SearchPage;
