// pages/StatisticsDashboard.js

import { useEffect, useState } from 'react';
import neo4j from 'neo4j-driver';
import './StatisticsDashboard.css';

const driver = neo4j.driver(
  'neo4j+s://2cd0ce39.databases.neo4j.io',
  neo4j.auth.basic('neo4j', 'nW1azrzTK-lrTOO5G1uOkUVFwelcQlEmKPHggPUB7xQ')
);

const totalPopulation = async () => {
  const session = driver.session();
  try {
    const result = await session.run(`
      MATCH (n:Person)
      RETURN count(n) AS total
    `);
    return result.records[0].get('total').toInt();
  } catch (error) {
    console.error("Error counting population:", error);
    return 0;
  } finally {
    await session.close();
  }
};

const oldestPerson = async () => {
  const session = driver.session();
  try {
    const result = await session.run(`
      MATCH (n:Person)
      WHERE n.YoB IS NOT NULL
      RETURN n
      ORDER BY n.YoB ASC
      LIMIT 1
    `);
    if (result.records.length > 0) {
      const person = result.records[0].get("n").properties;
      const age = new Date().getFullYear() - person.YoB;
      return {
        name: `${person.name || ''} ${person.lastName || ''}`.trim(),
        age
      };
    } else {
      return { name: "غير معروف", age: "-" };
    }
  } catch (error) {
    console.error("Error fetching oldest person:", error);
    return { name: "خطأ", age: "-" };
  } finally {
    await session.close();
  }
};

const youngestPerson = async () => {
  const session = driver.session();
  try {
    const result = await session.run(`
      MATCH (n:Person)
      WHERE n.YoB IS NOT NULL
      RETURN n
      ORDER BY n.YoB DESC
      LIMIT 1
    `);
    if (result.records.length > 0) {
      const person = result.records[0].get("n").properties;
      const age = new Date().getFullYear() - person.YoB;
      return {
        name: `${person.name || ''} ${person.lastName || ''}`.trim(),
        age
      };
    } else {
      return { name: "غير معروف", age: "-" };
    }
  } catch (error) {
    console.error("Error fetching youngest person:", error);
    return { name: "خطأ", age: "-" };
  } finally {
    await session.close();
  }
};

const averageAge = async () => {
    const session = driver.session();
    try {
      const result = await session.run(`
        MATCH (n:Person)
        WHERE n.YoB IS NOT NULL
        RETURN AVG(n.YoB) AS averageYoB
      `);
  
      if (result.records.length > 0) {
        const avgYoB = result.records[0].get('averageYoB').toNumber(); // correct alias name
        const currentYear = new Date().getFullYear();
        const averageAge = Math.round(currentYear - avgYoB);
        console.log("AVG AGE IS : "+averageAge);
        return { averageAge };
      } else {
        return { averageAge: "-" };
      }
    } catch (error) {
      console.error("Error fetching average age:", error);
      return { averageAge: "-" };
    } finally {
      await session.close();
    }
};
  
  
const StatisticsDashboard = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const total = await totalPopulation();
        const oldest = await oldestPerson();
        const youngest = await youngestPerson();
        const avgAge = await averageAge();

        const totalMen = 0;
        const totalWomen = 0;

        setStats({
          totalPopulation: total,
          totalMen,
          totalWomen,
          avgAge,
          oldestPerson: oldest,
          youngestPerson: youngest
        });
      } catch (error) {
        console.error('Error fetching stats:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) return <p>جاري تحميل الإحصائيات...</p>;
  if (!stats) return <p>تعذر تحميل البيانات.</p>;

  return (
    <div className="stats-dashboard">
      <h2>لوحة الإحصائيات</h2>
      <div className="stats-grid">
        <div className="stat-card">
          <h3>عدد السكان الإجمالي</h3>
          <p>{stats.totalPopulation}</p>
        </div>

        <div className="stat-card">
          <h3>عدد النساء</h3>
          <p>{stats.totalWomen}</p>
        </div>

        <div className="stat-card">
          <h3>عدد الرجال</h3>
          <p>{stats.totalMen}</p>
        </div>

        <div className="stat-card">
          <h3>متوسط العمر</h3>
          <p>{stats.averageAge} سنة</p>
        </div>

        <div className="stat-card">
          <h3>أكبر شخص سنًا</h3>
          <p>{stats.oldestPerson.name} ({stats.oldestPerson.age} سنة)</p>
        </div>

        <div className="stat-card">
          <h3>أصغر شخص</h3>
          <p>{stats.youngestPerson.name} ({stats.youngestPerson.age} سنة)</p>
        </div>
      </div>
    </div>
  );
};

export default StatisticsDashboard;
