  // Base return fields
  let returnStatement = `
    RETURN id(p) AS personID, 
           p.name AS personName, 
           p.lastName AS familyName, 
           COALESCE(father.name, 'Unknown') AS fatherName, 
           COALESCE(grandfather.name, 'Unknown') AS grandfatherName
  `;

  // Start query
  let query = `
    MATCH (p:Person)
    WHERE p.name = $name AND p.lastName = $lastName
  `;

  // Build MATCH clauses
  if (fatherName) {
    query += `
      MATCH (father:Person {name: $fatherName})-[:FATHER_OF]->(p)
    `;
  } else {
    query += `
      OPTIONAL MATCH (father:Person)-[:FATHER_OF]->(p)
    `;
  }

