    query += `
      MATCH (grandfather:Person {name: $grandfatherName})-[:FATHER_OF]->(father)
    `;
  } else {
    query += `
      OPTIONAL MATCH (grandfather:Person)-[:FATHER_OF]->(father)
    `;
  }

