  const uniqueQuery = `
    MATCH (p:Person)
    WHERE p.name = $name AND p.lastName = $lastName
    RETURN id(p) AS personID, p.name AS personName, p.lastName AS familyName
  `;

  let result = await session.run(uniqueQuery, { name, lastName });
