      name: record.get('personName'),
      lastName: record.get('familyName'),
      father: "Unknown",
      grandfather: "Unknown"
    }];
  }

  // Run full query if not unique
  const params = { name, lastName };
  if (fatherName) params.fatherName = fatherName;
  if (grandfatherName) params.grandfatherName = grandfatherName;

  const fullQuery = query + returnStatement;
  console.log("ðŸ“œ Final Query:\n", fullQuery);
  console.log("ðŸ“¦ Params:", params);

  result = await session.run(fullQuery, params);

  return result.records.map(record => ({
    id: record.get('personID').toNumber(),
    name: record.get('personName'),
    lastName: record.get('familyName'),
    father: record.get('fatherName'),
    grandfather: record.get('grandfatherName')
  }));
};


