// Function that split english translated name that may contain ben to full name only parts.
function splitName(fullName) {
  const parts = fullName.replace(/ ben /gi, ' ').split(/\s+/);
  if (parts.length === 2) {
    return {
      personName: parts[0],
      fatherName: "",
      grandfatherName: "",
      familyName: parts[1]
    };
  } else if (parts.length === 3) {
    return {
      personName: parts[0],
      fatherName: parts[1],
      grandfatherName: "",
      familyName: parts[2]
    };
  } else if (parts.length === 4) {
    return {
      personName: parts[0],
      fatherName: parts[1],
      grandfatherName: parts[2],
      familyName: parts[3]
    };
  }
  // Default case if structure doesn't match
  return {
    personName: parts[0],
    fatherName: "",
    grandfatherName: "",
    familyName: parts[1]
  };
}
