function countBenAndBent(str) {
  // 1. Split into words (any amount of whitespace)
  const words = str.trim().split(/\s+/);
  // 2. If there are fewer than 3 words, there can't be an ‚Äúinside‚Äù occurrence
  if (words.length < 3) return 0;
  // 3. Take only the ‚Äúinterior‚Äù words (drop first and last)
  const interior = words.slice(1, -1);
  // 4. Count how many are exactly "ÿ®ŸÜ" or "ÿ®ŸÜÿ™"
  return interior.filter(w => w === 'ÿ®ŸÜ' || w === 'ÿ®ŸÜÿ™').length;
}

