import React, { useState, useEffect } from 'react';
import neo4j from 'neo4j-driver';
import "./SearchPage.css"

const driver = neo4j.driver(
  'neo4j+s://2cd0ce39.databases.neo4j.io',  // URI of the Neo4j server
  neo4j.auth.basic('neo4j', 'nW1azrzTK-lrTOO5G1uOkUVFwelcQlEmKPHggPUB7xQ'));


const SearchPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [personDetails, setPersonDetails] = useState(null);
  const [error, setError] = useState('');

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const searchPerson = async (query) => {
    const session = driver.session();
    try {
      const result = await session.run(
        `
        MATCH (grandfather:Person)-[:FATHER_OF]->(father:Person)-[:FATHER_OF]->(child:Person)
        WHERE child.name = 'Moslem' AND child.lastName = 'Teyeb'
        RETURN father.name AS fatherName, grandfather.name AS grandfatherName, child.name AS childName, child.YoB AS childYoB, child.gender AS childGender

        `,
        { query }
      );

      if (result.records.length > 0) {
        const record = result.records[0];
        setPersonDetails({
          personName: record.get('childName'),
          fatherName: record.get('fatherName'),
          grandfatherName: record.get('grandfatherName'),
        //   familyName: record.get('familyName'),
          gender: record.get('childGender'),
          age: record.get('childYoB'),
        });
        setError('');
      } else {
        setPersonDetails(null);
        setError('لم يتم العثور على شخص مطابق.');
      }
    } catch (err) {
      console.error('Query Error:', err);
      setError('حدث خطأ أثناء البحث.');
      setPersonDetails(null);
    } finally {
      await session.close();
    }
  };

  useEffect(() => {
    if (searchQuery.trim().length > 0) {
        console.log(searchQuery.trim());
      searchPerson(searchQuery.trim());
    } else {
      setPersonDetails(null);
      setError('');
    }
  }, [searchQuery]);

  return (
    <div className="search-page">
      <header className="search-header">
        <h1>ابحث عن شخص في شجرة العائلة</h1>
        <input
          type="text"
          className="search-bar"
          placeholder="ابحث عن اسم الشخص واسم العائلة أو الثلاثي..."
          value={searchQuery}
          onChange={handleSearchChange}
        />
      </header>

      {error && <div className="error-message">{error}</div>}

      {personDetails ? (
        <div className="person-details">
          <h2>تفاصيل الشخص</h2>
          <p><strong>الاسم:</strong> {personDetails.personName}</p>
          <p><strong>اسم الأب:</strong> {personDetails.fatherName}</p>
          <p><strong>اسم الجد:</strong> {personDetails.grandfatherName}</p>
          {/* <p><strong>اسم العائلة:</strong> {personDetails.familyName}</p> */}
          <p><strong>الجنس:</strong> {personDetails.gender}</p>
          <p><strong>العمر:</strong> {personDetails.age} سنة</p>
          <p><strong>الأم:</strong> {personDetails.motherName}</p>
        </div>
      ) : (
        <div className="no-results">
          <p>لا توجد نتائج للبحث.</p>
        </div>
      )}
    </div>
  );
};

export default SearchPage;
