// src/components/WorldMap.js
import React from 'react';
import Plot from 'react-plotly.js';
import './WorldMap.css';  // Import the CSS file for styling

const WorldMap = () => {
  // Example data (replace with real Ksar population numbers)
  const countries = ['TN', 'FR', 'IT', 'US']; // ISO 3166-1 alpha-2 country codes
  const values = [2200, 370, 152, 80]; // Ksar population per country

  return (
    <div className="world-map-container">
      <h3>عدد أفراد الكُسَر حسب الدول</h3>
      <Plot
        data={[
          {
            type: 'choropleth',
            locationmode: 'ISO-3',
            locations: countries,
            z: values,
            colorscale: 'YlOrRd',
            colorbar: {
              title: 'عدد أفراد الكُسَر',
              tickvals: [0, 500, 1000, 1500, 2000],
              ticktext: ['0', '500', '1000', '1500', '2000+'],
            },
          },
        ]}
        layout={{
          title: 'توزيع الكُسَر حسب البلدان',
          geo: {
            projection: { type: 'natural earth' },
            showcoastlines: true,
            coastlinecolor: 'rgb(255, 255, 255)',
            showland: true,
            landcolor: 'rgb(242, 242, 242)',
            showlakes: true,
            lakecolor: 'rgb(255, 255, 255)',
          },
          margin: { t: 50, b: 0, l: 0, r: 0 },
        }}
        style={{ width: '100%', height: '600px' }}
      />
    </div>
  );
};

export default WorldMap;
